package com.aguosoft.app.videocamp.view;import android.content.Context;import android.util.AttributeSet;import android.widget.ImageView;public class SquareImageView extends ImageView{	public SquareImageView(Context context)	{		super(context);		init(context, null, 0);	}	public SquareImageView(Context context, AttributeSet attrs)	{		super(context, attrs);		init(context, attrs, 0);	}	public SquareImageView(Context context, AttributeSet attrs, int defStyleAttr)	{		super(context, attrs, defStyleAttr);		init(context, attrs, defStyleAttr);	}	public void init(Context context, AttributeSet attrs, int defStyleAttr)	{	}	@Override	protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec)	{		setMeasuredDimension(getDefaultSize(0, widthMeasureSpec), getDefaultSize(0, heightMeasureSpec));		int measured_w = getMeasuredWidth();		int measured_h = getMeasuredHeight();		int measured_size = Math.min(measured_w, measured_h);		widthMeasureSpec = MeasureSpec.makeMeasureSpec(measured_size, MeasureSpec.EXACTLY);		heightMeasureSpec = MeasureSpec.makeMeasureSpec(measured_size, MeasureSpec.EXACTLY);		super.onMeasure(widthMeasureSpec, heightMeasureSpec);	}}